var assert = require("assert")
describe('ES6 Compatibility', function(){
  describe('#Classes', function(){
    it('should create the class Foo and return 5 from the function main()', function(){
      var expected = 5;

      class Foo {
        constructor() {

        }

        main() {
          return 5;
        }
      }

      var foo = new Foo();
      var result = foo.main();

      assert.equal(expected, result);
    })
  })
})
